import React, {Component } from 'react'

import {onDigitPress,onOperationPress, onClear} from '../actions/ButtonActions'
import Button from '../containers/ButtonContainer'
// import './ButtonPanel.css'

class ButtonPanel extends Component{
    render(){
        return(
      //     <div className="keys">

      //   <span className="btn operator" actionCreator={onOperationPress}>+</span> 
      //   <span className="btn operator" actionCreator={onOperationPress}>-</span> 
      //   <span className="btn operator" actionCreator={onOperationPress}>×</span> 
      //   <span className="btn operator" actionCreator={onOperationPress}>&divide;</span>
      //   <span className="btn number" actionCreator={onDigitPress}>7</span> 
      //   <span className="btn number" actionCreator={onDigitPress}>8</span> 
      //   <span className="btn number" actionCreator={onDigitPress}>9</span> 
      //   <span className="btn operator" actionCreator={onOperationPress} >&radic;</span>
      //   <span className="btn number" actionCreator={onDigitPress}>4</span> 
      //   <span className="btn number" actionCreator={onDigitPress}>5</span> 
      //   <span className="btn number" actionCreator={onDigitPress}>6</span> 
      //   <span className="btn operator" actionCreator={onOperationPress}>x<sup>y</sup></span>
      //   <span className="btn number" actionCreator={onDigitPress}>1</span> 
      //   <span className="btn number" actionCreator={onDigitPress}>2</span> 
      //   <span className="btn number" actionCreator={onDigitPress}>3</span> 
      //   <span className="btn " id="pnsign">+/-</span>
      //   <span className="btn number" actionCreator={onDigitPress}>0</span> 
      //   <span className="btn number" actionCreator={onOperationPress}>.</span> 
      //   <span className="btn operator" id="eval" actionCreator={onOperationPress}>=</span> 
      //   <span className="btn clear" >C</span>
      // </div>

       <div  className=" keys ">
       <div>
        <span> <Button name="%" actionCreator={onOperationPress} /> </span>
         <Button name="÷" actionCreator={onOperationPress}  />
         <Button name="AC" actionCreator={onClear} orange />

       </div>
       <div>
       <Button name="sin" actionCreator={onOperationPress} orange />

         <Button name="7" actionCreator={onDigitPress} />
         <Button name="8" actionCreator={onDigitPress} />
         <Button name="9" actionCreator={onDigitPress} />
         <Button name="x" actionCreator={onOperationPress} orange />

       </div>
       <div>
       <Button name="cos" actionCreator={onOperationPress} orange />

         <Button name="4" actionCreator={onDigitPress} />
         <Button name="5" actionCreator={onDigitPress} />
         <Button name="6" actionCreator={onDigitPress} />
         <Button name="-" actionCreator={onOperationPress} orange />

       </div>
       <div>
       <Button name="tan" actionCreator={onOperationPress} orange />

         <Button name="1" actionCreator={onDigitPress} />
         <Button name="2" actionCreator={onDigitPress} />
         <Button name="3" actionCreator={onDigitPress} />
         <Button name="+" actionCreator={onOperationPress} orange />

       </div>
       <div>
         <Button name="Exp" actionCreator={onOperationPress} orange />
         <Button name="0" actionCreator={onDigitPress} wide />
         <Button name="." actionCreator={onOperationPress} />
         <Button name="=" actionCreator={onOperationPress} orange />

       </div>
     </div>
        );
    }
}


export default ButtonPanel;
